ucher_No, main.Voucher_Prefix, \'SALESVOUCHER\' as Voucher_Type, main.Voucher_Date as Date, concat(main.Voucher_No ,\'-\', main.Voucher_Prefix) as \'Voucher No\', (SELECT Ledger_Name from $master_db.Ledger_Master where Ledger_ID=main.Ledger_ID) as Ledger,det.quantity as Quantity, det.SubTotal as \'Total Amount\' From $trans_db.Sales_invoice_Main main JOIN Sales_Invoice_Details det ON main.voucher_no = det.voucher_no and main.voucher_prefix = det.voucher_prefix WHERE det.inventory_item_id = \'$f1\' AND (str_to_Date(concat(main.voucher_date, substr(timestamp, 11,19)) ,\'%Y-%m-%d %H:%i:%s\') >= str_to_date(\'$fromDateTime\',\'%Y-%m-%d %H:%i:%s\') and str_to_Date(concat(main.voucher_date, substr(timestamp, 11,19)) ,\'%Y-%m-%d %H:%i:%s\') <=str_to_date(\'$toDateTime\',\'%Y-%m-%d %H:%i:%s\') ) order BY cast(main.Voucher_No as unsigned) desc ','{\"filter by\":[{\"var-name\":\"f1\",\"var-value\":\"item\"}],\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[7,8], \"Hide\":[0,1,2,3], \"Key-col\":[1,2], \"width-list\":[10,10,30,20,20], \"align-list\":[0,0,0,1,1], \"print-cols\":[4,5,6,7,8], \"stretch\":6}',NULL,NULL),(3,'Itemwise Transaction Summary','Inventory','Reports','Inventory','SELECT \'I\' as Ind, ItemID, (SELECT Item_Name FROM $master_db.Sales_Inventory_Items WHERE Item_Id = ItemID) as \'Item\', SUM(Cr_Qty) as \'Cr Qty\', SUM(Dr_Qty) as \'Dr Qty\', SUM(Cr_Amount) as \'Credit Amount\', SUM(Dr_Amount) as \'Debit Amount\' FROM $trans_db.Transaction_Item_Helper WHERE addTime(VoucherDate, time) >= str_to_date(\'$fromDateTime\', \'%Y-%m-%d %H:%i:%s\') AND addTime(VoucherDate, time) <=str_to_date(\'$toDateTime\', \'%Y-%m-%d %H:%i:%s\') AND VoucherType like \'$f2\' GROUP BY ItemID ORDER BY Item','{\"filter by\":[{\"var-name\":\"f2\",\"var-value\":\"voucherType\"}],\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[3,4,5,6], \"Hide\":[0,1], \"width-list\":[20,20,20,20,20], \"align-list\":[0,1,1,1,1], \"print-cols\":[2,3,4,5,6], \"stretch\":0}','{\"redirect-to\":[{\"default\":{\"id\":5, \"keyList\":[{\"filter-type\":\"item\",\"var-name\":\"f1\", \"col-pos\":1}]}}]}',NULL),(4,'Groupwise Purchase Report','Inventory','Reports','Inventory','select Ind,ID,itemGrp.group_name as Name, SUM(Quantity) as Quantity, SUM(Subtotal) as Amount from $trans_db.Purchase_invoice_details det inner join (select ItemGrp1.group_name, Item.item_id, \'G\' as Ind,ItemGrp1.group_id as ID from $master_db.sales_inventory_items item inner join (with recursive cte (group_id, group_name, parent_id, item_id) as (select grp.group_id,grp.group_name,grp.parent_id, item.item_id from $master_db.sales_inventory_items item left outer join sales_inventory_groups grp on item.group_id = grp.group_id union all select p.group_id, p.group_name,p.parent_id, item_id from $master_db.sales_inventory_groups p inner join cte on p.group_id = cte.parent_id ) select cte.group_name, cte.item_id, cte.group_id from cte where cte.parent_id = \'$f1\' ) itemGrp1 on itemGrp1.item_id = item.item_id union select Item2.Item_name, Item2.Item_id, \'I\' as ind,Item2.Item_id as ID from $master_db.sales_inventory_items Item2 where Item2.group_id = \'$f1\' ) itemGrp on det.inventory_item_id = itemGrp.item_id where det.Voucher_no in (SELECT Voucher_No from $trans_db.Purchase_Invoice_Main main where  str_to_Date(concat(main.Voucher_Date, substr(`TimeStamp`, 11,19)) ,\'%Y-%m-%d %H:%i:%s\')>=str_to_date(\'$fromDateTime\',\'%Y-%m-%d %H:%i:%s\') and str_to_Date(concat(main.Voucher_Date, substr(`TimeStamp`, 11,19)) ,\'%Y-%m-%d %H:%i:%s\')<= str_to_date(\'$toDateTime\',\'%Y-%m-%d %H:%i:%s\') ) group by itemGrp.group_name ORDER BY 1,2','{\"filter by\":[{\"var-name\":\"f1\",\"var-value\":\"group\"}],\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[3,4], \"Hide\":[0,1], \"width-list\":[40,20,20], \"align-list\":[0,1,1], \"print-cols\":[2,3,4], \"stretch\":2}',' {\"redirect-to\":[{\"default\":{\"id\":5, \"keyList\":[{\"filter-type\":\"item\",\"var-name\":\"f1\", \"col-pos\":1}]}}]}\r\n',NULL),(5,'Item Movement Analysis','Inventory','Reports','Inventory','SELECT \'V\' as Ind, VoucherId, VoucherPrefix, VoucherType, date(VOUCHERDATE) as \'Voucher Date\' , cast(VOUCHERID as Integer) as \'Voucher No\' , VOUCHERTYPE as \'Voucher Type\' , (case when To_Location = \'OUT\' then (select ledger_name from $master_db.ledger_master where ledger_id = To_Ledger) else (select ledger_name from $master_db.ledger_master where ledger_id = From_Ledger) end ) as Ledger,  CAST(SUM(CR_QTY) AS DECIMAL(10,2)) as \'Outward\' , CAST(SUM(DR_QTY) AS DECIMAL(10,2)) as \'Inward\'  FROM $trans_db.TRANSACTION_ITEM_HELPER WHERE ITEMID = \'$f1\' AND VOUCHERDATE >= str_to_date(\'$fromDateTime\',\'%Y-%m-%d\') AND VOUCHERDATE <= str_to_date(\'$toDateTime\',\'%Y-%m-%d\')  AND VOUCHERTYPE like \'$f2\' GROUP BY  VOUCHERTYPE, VOUCHERID, VOUCHERPREFIX  ORDER BY 1, VOUCHERTYPE, 2, VOUCHERPREFIX','{\"filter by\":[{\"var-name\":\"f1\",\"var-value\":\"item\"}, {\"var-name\":\"f2\",\"var-value\":\"voucherType\"}],\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[8,9], \"Hide\":[0,1,2,3], \"width-list\":[20,20,30,20,20,20], \"align-list\":[0,0,0,0,1,1], \"print-cols\":[4,5,6,7,8,9], \"stretch\":7}',NULL,NULL),(6,'Negative Stock Report','Inventory','Reports','Inventory','SELECT \'I\' as Ind, itemID, INV.item_name as Item ,\r\ntrans.difference as Stock FROM $master_db.Sales_Inventory_Items as INV LEFT OUTER JOIN (SELECT itemID, \r\nSUM(COALESCE(Cr_Qty,0) -COALESCE(Dr_Qty,0)) AS difference FROM $trans_db.Transaction_Item_Helper \r\n  WHERE VoucherDate<= str_to_date(\'$toDateTime\',\'%Y-%m-%d\') Group BY ItemID)\r\n as trans on INV.Item_Id=trans.ItemID  WHERE trans.difference<0  AND Inv.isStockItem = 1 order by Item','{\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[3], \"Hide\":[0,1], \"width-list\":[20,20], \"align-list\":[0,1,0,1], \"print-cols\":[2,3], \"stretch\":2}','{\"redirect-to\":[{\"default\":{\"id\":5, \"keyList\":[{\"filter-type\":\"item\",\"var-name\":\"f1\", \"col-pos\":1}]}}]}',NULL),(7,'Groupwise Transaction Summary','Inventory','Reports','Inventory','select Ind,ID,itemGrp.group_name as Name, ABS(SUM(CR_QTY - DR_QTY)) as Quantity, ABS(SUM(CR_AMOUNT - DR_AMOUNT)) as Value  from $trans_db.Transaction_Item_Helper det  inner join  (select ItemGrp1.group_name, Item.item_id, \'G\' as Ind,ItemGrp1.group_id as ID from  $master_db.Sales_Inventory_Items item inner join  (with recursive cte (group_id, group_name, parent_id, item_id) as (  select     grp.group_id,grp.group_name,grp.parent_id, item.item_id  FROM $master_db.Sales_Inventory_Items item left outer join Sales_Inventory_Groups  grp  on item.group_id = grp.group_id  union all   select     p.group_id, p.group_name,p.parent_id, item_id  from       $master_db.Sales_Inventory_Groups  p  inner join cte   on p.group_id = cte.parent_id ) select cte.group_name, cte.item_id, cte.group_id  from cte where cte.parent_id = \'$f2\' ) itemGrp1  on itemGrp1.item_id = item.item_id  union  select Item2.Item_name, Item2.Item_id, \'I\' as ind,Item2.Item_id as ID  from  $master_db.Sales_Inventory_Items Item2  where Item2.group_id = \'$f2\' ) itemGrp  on det.ItemID = itemGrp.item_id  where  ( str_to_Date(addTime(det.VoucherDate, det.Time) ,\'%Y-%m-%d %H:%i:%s\')>=str_to_date(\'$fromDateTime\',\'%Y-%m-%d %H:%i:%s\') and str_to_Date(addTime(det.VoucherDate, det.Time) ,\'%Y-%m-%d %H:%i:%s\')<= str_to_date(\'$toDateTime\',\'%Y-%m-%d %H:%i:%s\')) AND det.voucherType like \'$f1\' group by itemGrp.group_name ORDER BY 1,3','{\"filter by\":[{\"var-name\":\"f2\",\"var-value\":\"group\"}, {\"var-name\":\"f1\",\"var-value\":\"voucherType\"}],\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[3,4], \"Hide\":[0,1], \"width-list\":[40,20,20], \"align-list\":[0,1,1], \"print-cols\":[2,3,4], \"stretch\":2}','{\"redirect-to\":[{\"default\":{\"id\":5, \"keyList\":[{\"filter-type\":\"item\",\"var-name\":\"f1\", \"col-pos\":1}]}}]}',NULL),(8,'Item Movement Summary By Ledger','Inventory','Reports','Inventory','SELECT \'I\' as Ind, trans.ItemID, (SELECT ITEM_NAME FROM $master_db.Sales_Inventory_Items WHERE ITEM_ID = TRANS.ITEMID) AS ITEM,CR_QTY as Cr,DR_QTY as Dr, COALESCE(SUM(CR_QTY),0) AS CREDIT, COALESCE(SUM(DR_QTY),0) AS DEBIT  FROM $trans_db.TRANSACTION_ITEM_HELPER TRANS WHERE FROM_LEDGER = \'$f1\' OR TO_LEDGER = \'$f1\' AND TRANS.VOUCHERDATE >= str_to_date(\'$fromDateTime\',\'%Y-%m-%d\') AND TRANS.VOUCHERDATE <= str_to_date(\'$toDateTime\',\'%Y-%m-%d\') GROUP BY ITEMID','{\"filter by\":[{\"var-name\":\"f1\",\"var-value\":\"ledger\"}],\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[3,4], \"Hide\":[0,1], \"width-list\":[20,20,20,20,20], \"align-list\":[0,1,1,1,1], \"print-cols\":[2,3,4,5,6], \"stretch\":2}','{\"redirect-to\":[{\"default\":{\"id\":28, \"keyList\":[{\"filter-type\":\"item\",\"var-name\":\"f1\", \"col-pos\":1}, {\"var-name\":\"f2\", \"var-value\":\"f1\"}]}}]}',NULL),(9,'Re-Order Report','Inventory','Reports','Inventory','SELECT \'I\' as Ind, inv.Item_Id as ID,inv.Item_Name as Item, inv.Reorder_Level as Level,  coalesce(SO.qty,0) as \'Sales Order\\n Pending\',  inv.Reorder_Level + COALESCE(SO.qty,0) as \'Required\\n Total\',  coalesce(stock,0) as \'Current Stock\'  FROM $master_db.Sales_Inventory_Items inv  left outer join ( select itemID, round(sum(debitQty - creditQty),3) as stock from (  select trans.VoucherDate, ItemID,  sum(trans.Cr_Qty) as creditQty, sum(trans.Dr_Qty) as debitQty, \'purchase\' as type  from $trans_db.Transaction_Item_Helper trans where VoucherDate <= str_to_date(\'$fromDateTime\',\'%Y-%m-%d\') group by ItemID UNION ALL  select trans.VoucherDate, ItemID,  sum(trans.Cr_Qty), sum(trans.Dr_Qty), \'sales\' as type  from $trans_db.Transaction_Bom_Helper trans where VoucherDate <= str_to_date(\'$toDateTime\',\'%Y-%m-%d\') group by ItemID ) A group by itemID ) stk  on inv.item_id = stk.itemID  left outer join  ( select inventory_item_id, sum(qty) as qty from  (select inventory_item_id , sum(quantity) as qty from $trans_db.sales_order_details  where itemProductionStatus = 110 group by inventory_item_id union select ing.purchase_item, sum(quantity) * ing.purchase_item_qty / ing.sales_item_qty from  $trans_db.sales_order_Details inner join  $master_db.ingredients  ing on inventory_item_id = ing.sales_item  where itemProductionStatus = 110 group by purchase_item) X  group by inventory_item_id ) SO on inv.Item_ID = SO.inventory_item_id WHERE isPurchaseItem = 1 and coalesce(Reorder_Level ,0) + coalesce(SO.qty ,0) - coalesce(stock ,0) > 0  ORDER BY Item','{\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[4,5,6], \"Hide\":[0,1], \"width-list\":[20,20,20,20,20], \"align-list\":[0,1,1,1,1], \"print-cols\":[2,3,4,5,6], \"stretch\":0}','{\"redirect-to\":[{\"default\":{\"id\":5, \"keyList\":[{\"filter-type\":\"item\",\"var-name\":\"f1\", \"col-pos\":1}]}}]}','[{\"widget-type\":\"QtMaterialFlatButton\", \"Layout\":\"filterLayout\", \"widget-id\":1, \"text\":\"Export to Purchase Order\", \"action\":\"ExportToPurchaseOrder\", \"widget-align\":\"AlignRight\", \"col-pos\":{\"itemID\": 1, \"stock\": 5, \"reqTotal\": 6}}]'),(10,'Godownwise Sales Report','Inventory','Reports','Inventory','SELECT \'I\' as Ind, ItemID,  ( SELECT Item_Name FROM $master_db.Sales_Inventory_Items where item_id = itemID ) as Item,  SUM(CR_QTY) as Quantity, SUM(Cr_Amount) as Value  FROM $trans_db.transaction_item_helper  Where from_location like \'$f1\' AND VoucherDate >= str_to_date(\'$fromDateTime\',\'%Y-%m-%d\') AND VoucherDate <= str_to_date(\'$toDateTime\',\'%Y-%m-%d\') group by ItemID','{\"filter by\":[{\"var-name\":\"f1\",\"var-value\":\"godown\"}],\"dateTime\":[\"fromDateTime\",\"toDateTime\"]}','{\"Totals\":[3,4], \"Hide\":[0,1], \"width-list\":[40,20,20], \"align-list\":[0,1,1], \"print-cols\":[2,3,4], \"stretch\":2}','{\"redirect-to\":[{\"default\":{\"id\":29, \"keyList\":[{\"filter-type\":\"item\",\"var-name\":\"f1\", \"col-pos\":1}, {\"var-name\":\"f2\", \"var-value\":\"f1\"}]}}]}',NULL),(11,'M